import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import Home from './Pages/home';
import Header from './Components/header';
import Footer from './Components/footer';
import Login from './Pages/login';
import Signup from './Components/signup';
import ForgetPassword from './Components/forgetPassword';
import JobList from './Pages/jobList';
import PostJob from './Pages/postJob';
import Toast from 'react-bootstrap/Toast';
import ToastContainer from 'react-bootstrap/ToastContainer'

class App extends React.Component {

  constructor(props) {
    super(props);
    this.ShowToaster = this.ShowToaster.bind(this);

    var toast = {toastShow: false,toastMsg: "",toastBG:"success"};
    this.state = {toast:toast,loggedUser:[]};
  }

  ShowToaster(val){
    var toast = {toastShow:val.toastShow,toastMsg: val.toastMsg,toastBG:val.toastBG};
    this.setState({toast:toast});
  }

  render(){
    const isLoggedIn = ((!localStorage.getItem('loggedUserToken'))?false:true);

    return (
      <div className="wrapper">
        <Router>
          <Header />

          <Routes>
            {
              !isLoggedIn ?
                <PublicRoute></PublicRoute> :
                null
            }
          </Routes>
        </Router>

        <ToastContainer className="p-3" position="top-end">
          <Toast 
          className="d-inline-block m-1" 
          onClose={() => {
            var toast = {toastShow:false,toastMsg: '',toastBG:"success"};
            return this.setState({toast:toast});
          }} 
          show={this.state.toast.toastShow} bg={this.state.toast.toastBG} delay={3000} autohide
          >
            <Toast.Header>
              <strong className="me-auto">Alert!</strong>
            </Toast.Header>
            <Toast.Body>{this.state.toast.toastMsg}</Toast.Body>
          </Toast>
        </ToastContainer>

        {/* <Footer /> */}
      </div>
    )
  }
}

class PublicRoute extends React.Component {
  render(){
    return(
      <>
        <Route exact path="/" element={<Home/>} />
        <Route path="/login" element={<Login callToaster={this.ShowToaster} />} />
        <Route path="/signup" element={<Signup/>} />
        <Route path="/forgetPass" element={<ForgetPassword/>} />
      </>
    )
  }
}

class PrivteRoute extends React.Component {
  render(){
    return(
      <>
        <Route exact path="/" element={<JobList/>} />
        <Route path="/jobList" element={<JobList/>} />
        <Route path="/postJob" element={<PostJob/>} />
      </>
    )
  }
}

export default App;
