import React from 'react';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import loginUser from '../API/login';
import axios from 'axios';

class login extends React.Component{
    constructor(props){
        super(props);

        this.submitLogin = this.submitLogin.bind(this);
    }

    submitLogin(e) {
        e.preventDefault();

        var loginAPI = axios.post(
            'https://jobs-api.squareboat.info/api/v1/auth/login',
            {
                email : e.target.elements.email.value,
                password : e.target.elements.password.value
            }
        );
        console.log(loginAPI);
    }

    render(){
        return(
            <div>
                <section id="featured-services" className="featured-services">
                    <div className="container">
                        <div className="row justify-content-md-center">
                            <div className="col-lg-6 col-md-6">
                            <Card>
                                <Card.Header>Login</Card.Header>
                                <Card.Body>
                                <Form onSubmit={this.submitLogin} >
                                    <Form.Group className="mb-3">
                                        <Form.Label>Email address</Form.Label>
                                        <Form.Control name="email" type="email" placeholder="Enter email" required />

                                    </Form.Group>

                                    <Form.Group className="mb-3">
                                        <Form.Label>Password</Form.Label>
                                        <Form.Control name="password" type="password" placeholder="Password" required />
                                    </Form.Group>
                                    <Form.Group className="mb-3">
                                        <a className="scrollto" href="/forgetPass">Forgot Your Password?</a>
                                    </Form.Group>
                                    <div className="row justify-content-md-center mb-3">
                                        <div className="col-lg-2 col-md-6">
                                            <Button variant="primary" type="submit">
                                                Submit
                                            </Button>
                                        </div>
                                    </div>

                                    <div className="row justify-content-md-center">
                                        <div className="col-lg-8 col-md-8">
                                            <Form.Group className="mb-3">
                                                <Form.Label>New to MyJobs?</Form.Label>
                                                <a className="scrollto" href="/signup">Create an account</a>
                                            </Form.Group>
                                        </div>
                                    </div>


                                </Form>
                                </Card.Body>
                            </Card>
                            </div>
                        </div>
                    </div>
                </section>

            </div>
        )
    }
}

export default login;
