import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import Home from './Pages/home';
import Header from './Components/header';
import Footer from './Components/footer';
import Login from './Pages/login';
import Signup from './Components/signup';
import ForgetPassword from './Components/forgetPassword';
import JobList from './Pages/jobList';
import PostJob from './Pages/postJob';
import Toast from 'react-bootstrap/Toast';
import ToastContainer from 'react-bootstrap/ToastContainer'

class App extends React.Component {

  constructor(props) {
    super(props);
    this.ShowToaster = this.ShowToaster.bind(this);
    this.state = {toastShow: false,toastMsg: "",toastBG:"success"};
  }

  ShowToaster(val){
    this.setState({toastShow:val.toastShow,toastMsg: val.toastMsg,toastBG:val.toastBG});
  }

  render(){
    return (
      <div className="wrapper">
        <Router>
          <Header />

          <Routes>
            <Route exact path="/" element={<Home/>} />
            <Route path="/login" element={<Login callToaster={this.ShowToaster}/>} />
            <Route path="/signup" element={<Signup/>} />
            <Route path="/forgetPass" element={<ForgetPassword/>} />
            <Route path="/jobList" element={<JobList/>} />
            <Route path="/postJob" element={<PostJob/>} />
          </Routes>
        </Router>
        <ToastContainer className="p-3" position="top-end">
          <Toast className="d-inline-block m-1" onClose={() => this.setState({toastShow:false,toastMsg: ''})} show={this.state.toastShow} bg={this.state.toastBG} delay={3000} autohide>
            <Toast.Header>
              <strong className="me-auto">Alert!</strong>
            </Toast.Header>
            <Toast.Body>{this.state.toastMsg}</Toast.Body>
          </Toast>
        </ToastContainer>

        {/* <Footer /> */}
      </div>
    )
  }
}

export default App;
