import React from 'react';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Pager from '../Components/pager';

class postJob extends React.Component{

    constructor(props){
        super(props);
        this.state = {show : false};

        this.handleClick = this.handleClick.bind(this);
        this.handleClose = this.handleClose.bind(this);
    }

    handleClick() {
        this.setState({show: true});
    }

    handleClose() {
        this.setState({show: false});
    }

    render(){
        return(
            <div>
                <section id="featured-services" className="featured-services">
                    <div className="container">
                        <div className="row justify-content-md-center">
                            <div className="col-lg-6 col-md-6">
                            <Card>
                                <Card.Header>Post a Job</Card.Header>
                                <Card.Body>

                                <Form>
                                    <Form.Group className="mb-3">
                                        <Form.Label>Job Title</Form.Label>
                                        <Form.Control type="text" placeholder="Enter Job Title" />

                                    </Form.Group>
                                    <Form.Group className="mb-3">
                                        <Form.Label>Description</Form.Label>
                                        <Form.Control as="textarea" rows={3} placeholder="Enter Job Description" />

                                    </Form.Group>

                                    <div className="row mb-3">
                                        <div className="col-md-6 col-md-6">
                                            <Form.Group className="mb-3">
                                                <Form.Label>Create Password</Form.Label>
                                                <Form.Control type="password" placeholder="Create Password" />
                                            </Form.Group>
                                        </div>
                                        <div className="col-md-6 col-md-6">
                                            <Form.Group className="mb-3">
                                                <Form.Label>Confirm Password</Form.Label>
                                                <Form.Control type="password" placeholder="Confirm Password" />
                                            </Form.Group>
                                        </div>
                                    </div>

                                    <Form.Group className="mb-3" >
                                        <Form.Label>Skills</Form.Label>
                                        <Form.Control as="textarea" rows={3} placeholder="Enter comma seperated skills here" />
                                    </Form.Group>

                                    <div className="row justify-content-md-center mb-3">
                                        <div className="col-lg-2 col-md-6">
                                            <Button variant="primary" type="submit">
                                                Submit
                                            </Button>
                                        </div>
                                    </div>

                                    <div className="row justify-content-md-center">
                                        <Form.Group className="col-md-6 mb-3 justify-content-center">
                                            <Form.Label>Have an account?</Form.Label>
                                            <a className="scrollto" href="/login">login</a>
                                        </Form.Group>
                                    </div>
                                </Form>
                                </Card.Body>
                            </Card>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}

export default postJob;
