import React from 'react';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import loginUser from '../API/login';

function forgetPassword(props){

    let forgetUser = async e => {
        e.preventDefault();

        await loginUser.post(
            '/auth/resetpassword',
            {
                email : e.target.elements.emailAddr.value,
                userRole: 1, 
                password: e.target.elements.pass.value,
                confirmPassword: e.target.elements.confirmPass.value,
                name: e.target.elements.fName.value,
                skills: e.target.elements.skills.value
            }
        )
        .then((response) => {
            if(response.data.success){
                props.callToaster({toastShow:true,toastMsg: "User Registered Successfully!",toastBG:"success"});
            }
        })
        .catch((err)=>{
            props.callToaster({toastShow:true,toastMsg: "Something went wrong!",toastBG:"danger"});
        });

    }

    return(
        <div>
            <section id="featured-services" className="featured-services">
                    <div className="container">
                        <div className="row justify-content-md-center">
                            <div className="col-lg-6 col-md-6">
                            <Card>
                                <Card.Header>Forgot Your Password?</Card.Header>
                                <Card.Body>
                                <Form onSubmit={forgetUser}>
                                    <Form.Group className="mb-3 text-muted">
                                        <Form.Label>Enter the email associated with your account and we'll send you instructions to reset your password.</Form.Label>
                                    </Form.Group>
                                    <Form.Group className="mb-3">
                                        <Form.Label>Email Address</Form.Label>
                                        <Form.Control name="email" type="email" placeholder="Enter email" />

                                    </Form.Group>

                                    <Button variant="primary" type="submit">
                                        Submit
                                    </Button>
                                </Form>
                                </Card.Body>
                            </Card>
                            </div>
                        </div>
                    </div>
                </section>
        </div>
    );

}

export default forgetPassword;
